import React from 'react';
import Header from '../components/Header'; 
import { withRouter, Link } from "react-router-dom";
import { connect } from "unistore/react";
import { actions } from "../store/store";
import '../style/bootstrap.min.css'
import '../style/home.css'

class Recipe extends React.Component{
    componentDidMount = async () => {
        this.props.setChange('data', undefined)
        const index = await this.props.match.params.index;
        const listRecipe = await this.props.listRecipe;
        const currentRecipe = listRecipe[index];
        await this.props.setChange('data', currentRecipe)
    }

    render() {
        const recipe = this.props.data
        if(recipe!==undefined){
            console.log(recipe)
        }
        return(
            <React.Fragment>
                <Header/>
                {recipe!==undefined?
                <div>
                    <img src={recipe} alt=""/>
                </div>
                :
                null
                }
                <div>

                </div>
            </React.Fragment>
        )
    }
}

export default connect("listRecipe, data", actions)(withRouter(Recipe))
