import React from 'react'
import Header from '../components/Header'
import { withRouter } from "react-router-dom";
import { connect } from "unistore/react";
import { actions } from "../store/store";
import '../style/bootstrap.min.css'
import '../style/home.css'
import background from '../images/background.jpg'

class Home extends React.Component {
    render () {
        return (
            <React.Fragment>
                <Header/>
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-md-12 title">
                            <img src={background} alt="ingredient">
                                <form onSubmit={e => e.preventDefault()}>
                                    <input className="form-control search-form"
                                        type="search" 
                                        name="search"
                                        placeholder="Search"
                                        aria-label="Search" 
                                        value={this.props.search}
                                        onChange={event => this.props.handleInput(event)}
                                        />
                                    <button className="btn btn-outline-secondary my-2 my-sm-0 search-btn" type="submit">
                                        <i className='material-icons'>search</i>
                                    </button>
                                </form>
                            </img>
                        </div>
                    </div>
                </div>
            </React.Fragment>

        )
    }
}
export default connect('dummy',actions)(withRouter(Home));
