import React from 'react';
import Header from '../components/Header'; 
import ListRecipe from '../components/ListRecipe';
import { withRouter, Link } from "react-router-dom";
import { connect } from "unistore/react";
import { actions } from "../store/store";
import '../style/bootstrap.min.css'
import '../style/home.css'
import background from '../images/background.jpg'
import logo from '../images/logo-orange.svg'

const urlHeadLine = "https://api.edamam.com/search?app_id=7173ea48&app_key=609f58237cd3b846b334f7b7e3f681b2&q="

class Home extends React.Component {
    handleSearch = async () =>{
        const keyword = await this.props.search
        console.log(keyword)
        await this.props.handleGetApi(urlHeadLine+keyword)
        const data = await this.props.data
        console.log(this.data)
        while(data===undefined){
            const dict = {
                isLoading: false,
                listArticle: data.hits
            }
            await this.props.setManyChanges(dict)
        }
    }

    render () {
        let recipeToShow;
        if(this.props.listArticle!==undefined){
            const listArticle = this.props.listArticle;
            recipeToShow = listArticle.map((item)=>{
                return(
                    <ListRecipe
                    image={item.recipe.image}
                    title={item.recipe.label}
                    />
                )
            })
        }
        return (
            <React.Fragment>
                <Header/>
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-md-12 title">
                            <img src={background} alt="ingredient"/>
                            <div className="title-overlay"></div>
                            <div className="title-logo">
                                <img src={logo} alt="logo-recipe"/>
                                <h1>Recipe.com</h1>
                            </div>
                            <form className="form-inline my-2 my-lg-0 search">
                                <input className="form-control search-form"
                                    type="search" 
                                    name="search"
                                    placeholder="Search"
                                    aria-label="Search" 
                                    value={this.props.search}
                                    onChange={event => this.props.setInput(event)}
                                    />
                                <Link className="btn my-2 my-sm-0 search-btn"
                                    onClick={this.handleSearch}>
                                    <i className='material-icons'>search</i>
                                </Link>
                            </form>
                        </div>
                    </div>
                    {this.props.isLoading?
                    null
                    :
                    recipeToShow
                    }
                </div>
            </React.Fragment>

        )
    }
}
export default connect('isLoading, data, search',actions)(withRouter(Home));
